import React, { useReducer, useState } from 'react';
import './App.css';

const formReducer = (state, event) => {
  if(event.reset) {
   return {
     apple: '',
     count: 0,
     name: '',
     phoneNumber: '',
     email: '',
     number:'',
     address:'',
   }
 }
 return {
   ...state,
   [event.name]: event.value
 }
}

function App() {
  const [formData, setFormData] = useReducer(formReducer, {
    count: 100,
  });
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = event => {
   event.preventDefault();
   setSubmitting(true);

   setTimeout(() => {
     setSubmitting(false);
     setFormData({
       reset: true
     })
   }, 3000)
 }

 const handleChange = event => {
   const isCheckbox = event.target.type === 'checkbox';
    setFormData({
      name: event.target.name,
      value: isCheckbox ? event.target.checked : event.target.value,
    });
  }

  return (
    <div className="wrapper">
      <h1>Registration </h1>
      {submitting &&
       <div>
          You are submitting the following:
          <ul>
            {Object.entries(formData).map(([name, value]) => (
            <li key={name}><strong>{name}</strong>:{value.toString()}</li>
            ))}
          </ul>
       </div>
     }
      <form onSubmit={handleSubmit}>
      <fieldset disabled={submitting}>
         <label>
           <p>First Name</p>
           <input name="First Name" onChange={handleChange} value={formData.name || ''}/>
         </label>
         <label>
           <p>Last Name</p>
           <input name="Last Name" onChange={handleChange} value={formData.name || ''}/>
         </label>
      </fieldset>
      <fieldset disabled={submitting}>
        <label>
          <p>NPI Number</p>
          <input name="NPI Number" onChange={handleChange} value={formData.number || ''}/>
        </label>
        <label>
          <p>Business Address</p>
          <input name="Business Address" onChange={handleChange} value={formData.address || ''}/>
        </label>
        <label>
          <p>Phone Number</p>
          <input name="Phone Number" onChange={handleChange} value={formData.phoneNumber || ''}/>
        </label>
        <label>
          <p>Email</p>
          <input name="Email" onChange={handleChange} value={formData.email || ''}/>
        </label>
      </fieldset>
      <button type="submit" disabled={submitting}>Submit</button>
      </form>
    </div>
  )
}

export default App;
